import { SET_ALERT, REM0VE_ALERT } from '../types';

const alertReducer = (state, action) => {
  switch (action.type) {
    case SET_ALERT:
      return action.payload;
    case REM0VE_ALERT:
      return null;

    default:
      return state;
  }
};

export default alertReducer;
